let flag=!1
function openNav(){document.getElementById("nav-mobile").style.width="80%"}
function closeNav(){document.getElementById("nav-mobile").style.width="0%"}
const openSideNav=()=>{var x=window.matchMedia("(max-width: 1024px)")
if(x.matches){document.querySelector('#sidenav').style.width='100%'}else{document.querySelector('#sidenav').style.width='50%'}
document.querySelector('#toogleSideNav').onclick=closeSideNav
document.onclick=function(e){if(e.target.getAttribute('id')!=='toogleSideNav'){closeSideNav()}}}
const closeSideNav=()=>{document.querySelector('#toogleSideNav').onclick=openSideNav
document.querySelector('#sidenav').style.width='0%'}